import os
import re

# Count test files and tests
test_count = 0
for root, dirs, files in os.walk('tests'):
    for f in files:
        if f.startswith('test_') and f.endswith('.py'):
            test_count += 1

# Count source files
src_count = 0
src_lines = 0
for root, dirs, files in os.walk('src'):
    for f in files:
        if f.endswith('.py'):
            src_count += 1
            try:
                with open(os.path.join(root, f), 'r', encoding='utf-8') as file:
                    src_lines += len(file.readlines())
            except:
                pass

print(f'源代码文件数: {src_count}')
print(f'源代码行数: {src_lines}')
print(f'测试文件数: {test_count}')
